const SkillCard = ({ skill, index }: { skill: any; index: number }) => {
  const [isVisible, setIsVisible] = useState(false);
  const cardRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.2 }
    );

    if (cardRef.current) {
      observer.observe(cardRef.current);
    }

    return () => observer.disconnect();
  }, []);

  return (
    <div
      ref={cardRef}
      className={`transform transition-all duration-700 ease-out ${
        isVisible
          ? "opacity-100 translate-y-0 scale-100"
          : "opacity-0 translate-y-12 scale-95"
      }`}
      style={{ transitionDelay: `${index * 100}ms` }}
    >
      <div className="h-full bg-slate-900/80 backdrop-blur-sm border border-slate-700/50 rounded-lg shadow-xl overflow-hidden relative group hover:shadow-blue-500/20 hover:border-blue-500/30 transition-all duration-300 hover:-translate-y-1 hover:scale-105">
        {/* Subtle gradient overlay */}
        <div className="absolute inset-0 bg-gradient-to-br from-blue-900/5 to-slate-800/5 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

        <div className="relative z-10 p-4">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-slate-800/50 border border-slate-600/30 shadow-lg transform transition-transform duration-300 group-hover:scale-110">
              {skill.logo}
            </div>
            <div>
              <h4 className="text-sm font-semibold text-white">{skill.name}</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const CategorySection = ({
  category,
  categoryIndex,
}: {
  category: any;
  categoryIndex: number;
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef(null);
  const CategoryIcon = category.icon;

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, []);

  return (
    <div
      ref={sectionRef}
      className={`mb-12 transform transition-all duration-800 ease-out ${
        isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
      }`}
      style={{ transitionDelay: `${categoryIndex * 200}ms` }}
    >
      {/* Category Header */}
      <div className="flex items-center gap-3 mb-6">
        <div
          className={`p-3 rounded-xl bg-gradient-to-r ${category.gradient} shadow-lg`}
        >
          <CategoryIcon className="w-6 h-6 text-white" />
        </div>
        <div>
          <h3 className="text-xl font-bold text-white">{category.title}</h3>
          <div
            className={`h-1 w-16 bg-gradient-to-r ${category.gradient} rounded-full mt-1`}
          ></div>
        </div>
      </div>

      {/* Skills Grid */}
      <div className="grid gap-4 grid-cols-2 sm:grid-cols-3 lg:grid-cols-4">
        {category.skills.map((skill: any, idx: number) => (
          <SkillCard key={idx} skill={skill} index={idx} />
        ))}
      </div>
    </div>
  );
};

export default function Skills() {
  const [headerVisible, setHeaderVisible] = useState(false);
  const [footerVisible, setFooterVisible] = useState(false);
  const headerRef = useRef(null);
  const footerRef = useRef(null);

  useEffect(() => {
    const observers: any = [];

    // Header observer
    const headerObserver = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setHeaderVisible(true);
        }
      },
      { threshold: 0.5 }
    );

    // Footer observer
    const footerObserver = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setFooterVisible(true);
        }
      },
      { threshold: 0.5 }
    );

    if (headerRef.current) {
      headerObserver.observe(headerRef.current);
      observers.push(headerObserver);
    }

    if (footerRef.current) {
      footerObserver.observe(footerRef.current);
      observers.push(footerObserver);
    }

    return () =>
      (observers as any[]).forEach((observer: any) => observer.disconnect());
  }, []);

  return (
    <section
      id="skills"
      className="py-16 px-6 bg-gradient-to-br from-slate-950 via-slate-900 to-black relative overflow-hidden"
    >
      {/* Enhanced background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-20 right-10 w-64 h-64 bg-blue-800/5 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 left-10 w-80 h-80 bg-purple-700/5 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-teal-600/3 rounded-full blur-3xl"></div>
      </div>

      <div className="relative z-10 max-w-6xl mx-auto">
        {/* Header */}
        <div
          ref={headerRef}
          className={`text-center mb-16 transform transition-all duration-800 ease-out ${
            headerVisible
              ? "opacity-100 translate-y-0"
              : "opacity-0 -translate-y-8"
          }`}
        >
          <div className="inline-flex items-center gap-3 mb-4 hover:scale-105 transition-transform duration-300">
            <Sparkles className="w-8 h-8 text-blue-400" />
            <h2 className="text-3xl md:text-4xl font-bold text-white">
              Technical Skills
            </h2>
            <Sparkles className="w-8 h-8 text-blue-400" />
          </div>
          <p
            className={`text-slate-400 text-lg transition-opacity duration-600 ${
              headerVisible ? "opacity-100" : "opacity-0"
            }`}
            style={{ transitionDelay: "300ms" }}
          >
            Technologies I master with passion and precision
          </p>
        </div>

        {/* Categories */}
        {skillCategories.map((category, idx) => (
          <CategorySection key={idx} category={category} categoryIndex={idx} />
        ))}

        {/* Bottom decoration */}
        <div
          ref={footerRef}
          className={`text-center mt-16 transform transition-all duration-600 ease-out ${
            footerVisible
              ? "opacity-100 translate-y-0"
              : "opacity-0 translate-y-5"
          }`}
          style={{ transitionDelay: "400ms" }}
        >
          <div className="flex items-center justify-center gap-4 text-slate-500">
            <div className="h-px bg-gradient-to-r from-transparent via-blue-500/50 to-transparent w-24"></div>
            <Sparkles className="w-5 h-5" />
            <span className="text-sm">Continuously learning and growing</span>
            <Sparkles className="w-5 h-5" />
            <div className="h-px bg-gradient-to-r from-transparent via-blue-500/50 to-transparent w-24"></div>
          </div>
        </div>
      </div>
    </section>
  );
}
